package com.example.lgfood_duan1.Activity;import androidx.annotation.NonNull;import androidx.appcompat.app.AppCompatActivity;import android.content.Intent;import android.os.Bundle;//import android.support.annotation.NonNull;import android.view.View;import android.widget.TextView;import android.widget.Toast;import com.example.lgfood_duan1.R;import com.google.android.gms.auth.api.signin.GoogleSignIn;import com.google.android.gms.auth.api.signin.GoogleSignInAccount;import com.google.android.gms.auth.api.signin.GoogleSignInClient;import com.google.android.gms.auth.api.signin.GoogleSignInOptions;import com.google.android.gms.tasks.OnCompleteListener;import com.google.android.gms.tasks.Task;import com.google.firebase.auth.FirebaseAuth;import org.jetbrains.annotations.NotNull;public class MainActivity extends AppCompatActivity {    GoogleSignInClient mGoogleSignInClient;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        TextView txt=findViewById(R.id.text123);        processrequest();        txt.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                mGoogleSignInClient.signOut().addOnCompleteListener(new OnCompleteListener<Void>() {                    @Override                    public void onComplete(@NonNull @NotNull Task<Void> task) {                        Toast.makeText(MainActivity.this, "SignOut", Toast.LENGTH_SHORT).show();                    }                });                startActivity(new Intent(getApplicationContext(),Sin_Up_Activity.class));                finish();            }        });    }    private void processrequest() {        GoogleSignInOptions gso = new GoogleSignInOptions                .Builder(GoogleSignInOptions.DEFAULT_SIGN_IN)                .requestIdToken(getString(R.string.default_web_client_id))                .requestEmail()                .build();        mGoogleSignInClient = GoogleSignIn.getClient(this, gso);    }}